import styled from 'styled-components';
import DrinkItem from './DrinkItem';
const StyledCategory = styled.section`
    width: 100%;

    h2 {
        font-size: 24px;
        font-weight: 400;
        color: #818181;
        margin-bottom: 10px;
    }
`;
const StyledDiv = styled.div`
    width: 100%;
    padding: 30px 0;
    border-top: 1px solid #d0d0d0;
    display: flex;
    justify-content: space-between;
    flex-flow: reverse;
`;

const CategorySection = () => (
    <StyledCategory>
        <h2>Ordinary Drink</h2>
        <StyledDiv>
            <DrinkItem />
            <DrinkItem />
            <DrinkItem />
            <DrinkItem />
            <DrinkItem />
            <DrinkItem />
            <DrinkItem />
            <DrinkItem />
            <DrinkItem />
            <DrinkItem />
        </StyledDiv>
    </StyledCategory>
);

export default CategorySection;
