import React from 'react';
import styled from 'styled-components';
import drink from '../images/drink.jpg';

const StyledDiv = styled.div`
    width: 19%;
    img {
        width: 150px;
        height: 150px;
        display: inline;
        text-align: center;
        margin-bottom: 20px;
    }
    h3 {
        font-size: 18px;
        font-weight: 400;
        text-align: center;
        color: #000;
    }
`;

const DrinkItem = () => (
    <StyledDiv>
        <img src={drink} alt="drink" />
        <h3>410 Gone</h3>
    </StyledDiv>
);

export default DrinkItem;
