import React, { useState, useEffect } from 'react';
import './App.css';
import endpoints from './api/endpoints';

import Header from './components/Header';
import BodyWrapper from './components/BodyWrapper';
import Filters from './components/Filters';
import Content from './components/Content';

function App() {
  const [filters, setFilters] = useState([]);
  const [drinks, setDrinks] = useState([]);
  useEffect(()=> {
    setInitialState();
  }, []);
  return (
    <div className="App">
      <Header />
      <BodyWrapper>
        <Filters filters={filters} />
        <Content drinks={drinks} />
      </BodyWrapper>
    </div>
  );

  async function setInitialState() {
    let filtersArray;
    fetch(endpoints.filters)
      .then(responce => responce.json())
      .then(array => {
        setFilters(array.drinks);
        filtersArray = array.drinks;
        return array;
      })
      .then(categories => {
        const drinksPromices = [];
        for (let category of categories.drinks) {
          drinksPromices.push(fetch(endpoints.drinks(category.strCategory)));
        };
        return Promise.all(drinksPromices);
      })
      .then(drinksPromices => {
        return Promise.all(drinksPromices.map(prom => prom.json()));
      })
      .then(drinksArray => {
        const initialState = drinksArray.map((drinks, index) => {
          return {
            filter: filtersArray[index].strCategory,
            drinks: [
              ...drinks.drinks
            ]
          }
        });
        setDrinks(initialState);
        return initialState;
      })
      .catch(error => console.error(error));
  }
}

export default App;
